package xyz.soongkun.roast.common.model;public class MailBean {    private String[] toEmails;    private String subject;    private String context;    public MailBean() {    }    public String[] getToEmails() {        return this.toEmails;    }    public void setToEmails(String[] toEmails) {        this.toEmails = toEmails;    }    public String getSubject() {        return this.subject;    }    public void setSubject(String subject) {        this.subject = subject;    }    public String getContext() {        return this.context;    }    public void setContext(String context) {        this.context = context;    }}