package xyz.soongkun.roast.module.t_user.service;import org.apache.commons.lang3.StringUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import xyz.soongkun.roast.module.t_user.dao.UserDao;import xyz.soongkun.roast.module.t_user.model.User;@Service("userService")public class UserServiceImpl implements UserService {    @Autowired    private UserDao userDao;    public UserServiceImpl() {    }    public void registerByEmail(User user) throws Exception {        if (StringUtils.isBlank(user.getEmail())) {            throw new Exception("email不能为空");        } else {            boolean isExist = this.userDao.checkEmailExist(user.getEmail());            if (!isExist) {                this.userDao.addUser(user);            } else {                throw new Exception("该email已经被注册了");            }        }    }    public User getUserByEmail(String email) {        return this.userDao.getUserByEmail(email);    }}