package xyz.soongkun.roast.util;import java.text.SimpleDateFormat;import java.util.Date;import java.util.Random;import org.apache.commons.lang3.StringUtils;public class TextUtil {    public TextUtil() {    }    public static boolean isEmail(String str) {        return StringUtils.isBlank(str) ? false : str.matches("^[a-z0-9]+([._\\-]*[a-z0-9])*@([a-z0-9]+[-a-z0-9]*[a-z0-9]+.){1,63}[a-z0-9]+$");    }    public static int getRandNum(int min, int max) {        int randNum = min + (int)(Math.random() * (double)(max - min + 1));        return randNum;    }    public static String getRandNum(int charCount) {        String charValue = "";        for(int i = 0; i < charCount; ++i) {            char c = (char)(randomInt(0, 10) + 48);            charValue = charValue + String.valueOf(c);        }        return charValue;    }    public static int randomInt(int from, int to) {        Random r = new Random();        return from + r.nextInt(to - from);    }    public static String getRandomFileName(String srcName) {        SimpleDateFormat simpleDateFormat = new SimpleDateFormat("yyyyMMdd");        Date date = new Date();        String str = simpleDateFormat.format(date);        Random random = new Random();        int rannum = (int)(random.nextDouble() * 90000.0D) + 10000;        return str + rannum + (srcName == null ? "" : srcName);    }}